mapper.insert:
/** org.openpaas.paasta.portal.api.mapper.autoscail.AppAutoScaleModalMapper.insertAppAutoScale */

    select COALESCE(max(no),0)+1 as no from auto_scaling_config

    insert into auto_scaling_config(
    no
    , guid
    , org
    , space
    , app
    , instance_min_cnt
    , instance_max_cnt
    , cpu_threshold_min_per
    , cpu_threshold_max_per
    , check_time_sec
    , memory_min_size
    , memory_max_size
    , auto_increase_yn
    , auto_decrease_yn
    )
    values (
    #{no}
    ,#{guid}
    ,#{org}
    ,#{space}
    ,#{app}
    ,#{instanceMinCnt}
    ,#{instanceMaxCnt}
    ,#{cpuThresholdMinPer}
    ,#{cpuThresholdMaxPer}
    ,#{checkTimeSec}
    ,#{memoryMinSize}
    ,#{memoryMaxSize}
    ,#{autoIncreaseYn}
    ,#{autoDecreaseYn}
    );

mapper.update:
/** org.openpaas.paasta.portal.api.mapper.autoscail.AppAutoScaleModalMapper.updateAppAutoScale */
update auto_scaling_config
SET

    guid = #{guid}



    ,instance_min_cnt = #{instanceMinCnt}


    ,instance_max_cnt = #{instanceMaxCnt}


    ,cpu_threshold_min_per = #{cpuThresholdMinPer}


    ,cpu_threshold_max_per = #{cpuThresholdMaxPer}


    ,check_time_sec = #{checkTimeSec}


    ,memory_min_size = #{memoryMinSize}


    ,memory_max_size = #{memoryMaxSize}


    ,auto_increase_yn = #{autoIncreaseYn}


    ,auto_decrease_yn = #{autoDecreaseYn}

WHERE 1=1

    AND guid = #{guid}
