mapper.select:
/* Documents.getDocumentsList */ SELECT
(select count(*) from documents where 1=1 ) as listLength, no, classification, title, user_id AS userId, use_yn AS useYn, file_name as fileName, file_path as filePath, file_size as fileSize, created, (SELECT value FROM portaldb.code_detail cd WHERE cd.key = classification AND cd.group_id = 'DOCUMENTS_CLASSIFICATION') as classificationValue FROM documents WHERE 1=1
ORDER BY created DESC LIMIT 10 offset #{pageOffset}

mapper.delete:
/* Documents.deleteDocument */
DELETE FROM documents
WHERE
no = #{no}
