mapper.insert:
/* SupportQnA.insertAnswer */
INSERT INTO portaldb.answer (content, file_name, file_path, file_size, created, lastmodified, question_no, answerer)
values(
    #{content},
    #{fileName},
    #{filePath},
    #{fileSize},
    now(),
    now(),
    #{questionNo},
    #{answerer}
);

mapper.update:
/* SupportQnA.updateQuestionStatus */
UPDATE portaldb.question q SET

    q.status = #{status},

q.lastModified = now()
WHERE 1=1

    AND q.no = #{questionNo}

/* SupportQnA.updateAnswer */ UPDATE portaldb.answer a SET
a.content = #{content},
a.file_name = #{fileName}, a.file_path = #{filePath}, a.file_size = #{fileSize}, a.answerer = #{answerer}, a.lastModified = now() WHERE 1=1
AND a.question_no = #{questionNo}

mapper.select:
/* SupportQnA.getMyQuestionList */ SELECT q.no AS "no", q.title AS title, q.classification AS classification, (SELECT cd.value FROM portaldb.code_detail cd WHERE cd.key = q.classification AND cd.group_id = 'QUESTION_CLASSIFICATION') AS classificationValue, q.user_id AS userId, q.content AS content, q.cell_phone AS cellPhone, q.status AS status, (SELECT cd.value FROM portaldb.code_detail cd WHERE cd.key = q.status AND cd.group_id = 'QUESTION_STATUS') AS statusValue, q.file_name AS fileName, q.file_path AS filePath, q.file_size AS fileSize,
(SELECT COUNT(1) FROM portaldb.question WHERE 1=1
AND user_id = #{userId}
AND title LIKE concat('%', #{searchKeyword},'%')
) AS totalCount,
q.created AS created, q.lastmodified AS lastModified FROM portaldb.question q WHERE 1=1
AND q.no = #{no}
AND q.user_id = #{userId}
AND q.title LIKE concat('%', #{searchKeyword},'%')
ORDER BY q.created DESC LIMIT #{pageSize} OFFSET #{pageNo}
