mapper.select:
/* CommonCode.getCommonCodeById */ SELECT cd.key AS "key", cd.value AS "value", cd.summary AS summary FROM code_detail cd WHERE 1=1
AND cd.group_id = #{id}
ORDER BY cd.order ASC

/* CommonCode.getCommonCodeDetail */ SELECT cd.key AS "key", cd.key AS orgKey, cd.value AS "value", cd.summary AS summary, cd.group_id AS groupId, cd.use_yn AS useYn, cd.order AS "order", cd.created AS created, cd.lastModified AS lastModified, cd.user_id AS userId, #{pageNo} AS pageNo, #{pageSize} AS pageSize, #{procType} AS procType FROM code_detail cd WHERE 1=1
AND cd.key = #{key}
AND cd.group_id = #{groupId}
ORDER BY cd.order ASC LIMIT #{pageSize} OFFSET #{pageNo}

/* CommonCode.getCommonCodeDetailCount */
SELECT
    COUNT(*)
FROM code_detail cd
WHERE 1=1

    AND cd.key = #{key}


    AND cd.group_id = #{id}


    AND cd.group_id = #{groupId}

mapper.update:
/* CommonCode.updateCommonCodeGroup */ UPDATE code_group SET
"id" = #{id},
"name" = #{name},
user_id = #{userId},
lastModified = now() WHERE "id" = #{orgId}

/* CommonCode.updateCommonCodeDetail */ UPDATE code_detail SET
"key" = #{key},
"value" = #{value},
summary = #{summary},
group_id = #{groupId},
use_yn = #{useYn},
"order" = #{order},
user_id = #{userId},
lastModified = now() WHERE 1=1
AND "key" = #{orgKey}
AND group_id = #{orgId}
AND group_id = #{groupId}

mapper.delete:
/* CommonCode.deleteCommonCodeDetail */
DELETE FROM code_detail
WHERE 1=1

    /* CommonCode.deleteCommonCodeDetail.key */
    AND "key" = #{key}


    /* CommonCode.deleteCommonCodeDetail.id */
    AND group_id = #{id}


    /* CommonCode.deleteCommonCodeDetail.groupId */
    AND group_id = #{groupId}
