mapper.select:
/* Menu.getMenuList */
SELECT
    (SELECT COUNT(1) FROM menu m1 WHERE m1.parent_no = m.no) AS childCount,

FROM menu m
WHERE 1=1

    AND m.no = #{no}


    AND m.parent_no = #{parentNo}


    AND m.parent_no = 0


    AND m.use_yn = #{useYn}

ORDER BY m.parent_no ASC, m.sort_no ASC, m.no ASC;

/* Menu.getMenuDetail */
SELECT

FROM menu m
WHERE m.no = #{no}
